import React, { Component } from "react";
import { serachReducer } from '../redux.js';
import { connect } from 'react-redux';


class SearchMain extends Component {
  constructor(props) {
    super(props);
    };


  render() {
      console.log(this.props.searchItems);
    const displayList=this.props.searchItems.map((items,index)=>{

     return <div>{items.name}</div>

    })

    return (
        <div>
        <div>
            <input onChange={this.handleChange} style={{ width: "150px", display: "inline-block" }} type="text"  placeholder="Enter Task" />
            <button  type="button" name="Search"/>
        </div>
        {displayList()}
        </div>
    );
  }
}

const mapStateToProps = (state) => ({
    searchItems: state.searchItems
})

const mapDispatchToProps = (dispatch) => ({
    serachReducer: (id) => dispatch(serachReducer(id))
})

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(SearchMain);
